import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    sometext: '',
    username: '',
    password: '',
    loginStatus: 'Enter your user name and password',
    loginStatusStyle: {
      color: 'black'
    }
  };
  // onChangeTextHandler = event => {
  //   this.setState({
  //     sometext: event.target.value
  //   });
  // };
  // onChangeUsernameHandler = event => {
  //   this.setState({
  //     username: event.target.value
  //   });
  // };
  // onChangePasswordHandler = event => {
  //   this.setState({
  //     password: event.target.value
  //   });
  // };

  onChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  /*
  create a button login that calls a function "login" and checks if the username is "hallo" and password is "world".
  if so, display a div underneath the login button stating with green that the login was successfull. otherwise . state 
  with red color that the login failed.
  */

  login = () => {
    this.state.username === 'hallo' && this.state.password === 'world'
      ? this.setState({
          loginStatus: 'logged in successfull',
          loginStatusStyle: {
            color: 'green'
          }
        })
      : this.setState({
          loginStatus: 'logged in failed',
          loginStatusStyle: {
            color: 'red'
          }
        });
  };
  render() {
    return (
      <div className="App">
        {/* comment */}
        <h1>Databinding 1</h1>
        <div>
          some text:
          <input name="sometext" type="text" onChange={this.onChange} />
        </div>
        <h3>You Entered: {this.state.sometext}</h3>

        <h1>Databinding 2</h1>
        <div>
          Username:{' '}
          <input name="username" onChange={this.onChange} type="text" />
        </div>
        <div>
          Username:{' '}
          <input name="password" onChange={this.onChange} type="password" />
        </div>

        <button onClick={this.login}>Login</button>
        <div style={this.state.loginStatusStyle}>{this.state.loginStatus}</div>
        <div />
      </div>
    );
  }
}

export default App;
