import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Fruit from './components/Fruit';

class App extends Component {
  state = {
    fruits: ['Apple', 'Mango', 'Banana', 'Lemon', 'Kiwi']
  };

  showColor = color => {
    alert('the color is ' + color);
  };

  deleteMe = item => {
    alert('clicked Delete Me from ' + item);
    let fruits2 = [];
    fruits2 = this.state.fruits.filter(e => e !== item);
    console.log(fruits2);
    this.setState({
      fruits: fruits2
    });
  };
  render() {
    return (
      <div className="App">
        {/* comment */}
        <h1>Components 1</h1>
        {this.state.fruits.map(fruit => {
          return (
            <Fruit
              type={fruit}
              onShowColorHandler={this.showColor}
              deletItemHandler={this.deleteMe}
            />
          );
        })}
      </div>
    );
  }
}

export default App;
