import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    fruits: [
      { name: 'Apple', id: 1 },
      { name: 'Banana', id: 2 },
      { name: 'Lemon', id: 3 },
      { name: 'Kiwi', id: 4 },
      { name: 'PineApple', id: 5 }
    ],
    inputValue: '',
    lastInsertId: 5
  };
  removeFruit = fruit => {
    const fruits = [...this.state.fruits];
    let fruits2 = [];
    fruits2 = fruits.filter(e => e.name !== fruit);
    console.log(fruits2);
    this.setState({
      fruits: fruits2
    });
  };

  addFruit = fruit => {
    this.setState({
      fruits: [
        ...this.state.fruits,
        { name: fruit, id: this.state.lastInsertId + 1 }
      ],
      lastInsertId: this.state.lastInsertId + 1
    });
  };
  updateInputValue = event => {
    this.setState({
      inputValue: event.target.value
    });
  };

  deleteMe = event => {
    console.log(event.target.parentnode.key);
  };
  render() {
    // task : convert the fruits array to an jsx array using the for loop and render it
    // let fruitsArr = [];
    // for (let index = 0; index < this.state.fruits.length; index++) {
    //   fruitsArr.push(<li>{this.state.fruits[index]}</li>);
    // }

    return (
      <div className="App">
        {/* comment */}

        <h1>Loops part 2</h1>
        <input type="text" onChange={this.updateInputValue} />

        <button onClick={this.removeFruit.bind(this, this.state.inputValue)}>
          Remove Fruit
        </button>
        <button onClick={this.addFruit.bind(this, this.state.inputValue)}>
          Add Fruit
        </button>
        <ul>
          {this.state.fruits.map(fruit => {
            return (
              <li key={fruit.id}>
                {fruit.name} <button onClick={this.deleteMe}>Delete</button>
              </li>
            );
          })}
        </ul>
        {/* <ul>{fruitsArr}</ul> */}
      </div>
    );
  }
}

export default App;
