import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    fruits: ['Apple', 'Banana', 'Lemon ', 'Kiwi', 'PineApple']
  };
  removeFruit = fruit => {
    const fruitIndex = this.state.fruits.findIndex(f => {
      return fruit === f; // returns if fruit is f
    });
    if (fruitIndex === -1) return;
    const fruits = [...this.state.fruits];
    fruits.splice(fruitIndex, 1);
    this.setState({
      fruits: fruits
    });
  };
  addFruit = fruit => {
    this.setState({
      fruits: [...this.state.fruits, fruit]
    });
  };
  render() {
    // task : convert the fruits array to an jsx array using the for loop and render it
    // let fruitsArr = [];
    // for (let index = 0; index < this.state.fruits.length; index++) {
    //   fruitsArr.push(<li>{this.state.fruits[index]}</li>);
    // }

    return (
      <div className="App">
        {/* comment */}
        <h1>Loops part 1</h1>
        <button onClick={this.removeFruit.bind(this, 'Apple')}>
          Remove Apple
        </button>
        <button onClick={this.addFruit.bind(this, 'Mango')}>Add Mango</button>
        <ul>
          {this.state.fruits.map(fruit => {
            return <li>{fruit}</li>;
          })}
        </ul>
        {/* <ul>{fruitsArr}</ul> */}
      </div>
    );
  }
}

export default App;
